$(function(){let flexContainer=$('div.flex-container');let searchBox=$('.search-box');let searchClose=$('.search-icon-close');let searchInput=$('#search-input');$('.menu-icon, .menu-icon-close').click(function(e){e.preventDefault();e.stopPropagation();flexContainer.toggleClass('active')});flexContainer.click(function(e){if(flexContainer.hasClass('active')&&e.target.tagName!=='A'){flexContainer.removeClass('active')}});$(window).keydown(function(e){if(e.key==='Escape'){if(flexContainer.hasClass('active')){flexContainer.removeClass('active')}else if(searchBox.hasClass('search-active')){searchBox.removeClass('search-active')}}});$('.search-icon').click(function(e){e.preventDefault();searchBox.toggleClass('search-active');searchInput.focus();if(searchBox.hasClass('search-active')){searchClose.click(function(e){e.preventDefault();searchBox.removeClass('search-active')})}})});function openCategory(evt,CategoryName){let i,tabcontent,tablinks;tabcontent=document.getElementsByClassName("tabcontent");for(i=0;i<tabcontent.length;i+=1){tabcontent[i].style.display="none"}tablinks=document.getElementsByClassName("tablinks");for(i=0;i<tablinks.length;i+=1){tablinks[i].className=tablinks[i].className.replace(" active","")}document.getElementById(CategoryName).style.display="block";evt.currentTarget.className+=" active"}document.getElementById("defaultOpen").click();
